

#create database books
create database if not exists books;

#use database books
use books;

#creating table titles
CREATE TABLE `titles` (
  `isbn` double NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`isbn`)
) ;

#creating table qualities
CREATE TABLE `qualities` (
  `isbn` double NOT NULL,
  `quality_rank` int(11) NOT NULL,
  `quality_name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`isbn`,`quality_rank`),
  CONSTRAINT `qualities_ibfk_1` FOREIGN KEY (`isbn`) REFERENCES `titles` (`isbn`)
) ;


#creating table themes
CREATE TABLE `themes` (
  `isbn` double NOT NULL,
  `theme_rank` int(11) NOT NULL,
  `theme_name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`isbn`,`theme_rank`),
  CONSTRAINT `themes_ibfk_1` FOREIGN KEY (`isbn`) REFERENCES `titles` (`isbn`)
) ;


#creating table authors
CREATE TABLE `authors` (
  `isbn` double NOT NULL,
  `author_rank` int(11) NOT NULL,
  `author_name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`isbn`,`author_rank`),
  CONSTRAINT `authors_ibfk_1` FOREIGN KEY (`isbn`) REFERENCES `titles` (`isbn`)
) ;

#creating table book_language
CREATE TABLE `book_language` (
  `language_id` int(11) NOT NULL,
  `language_name` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`language_id`)
) ;


#creating table book_properties
CREATE TABLE `book_properties` (
  `isbn` double NOT NULL,
  `language_id` int(1) NOT NULL,
  `is_award_winning` int(1) DEFAULT NULL,
  `published_date` date DEFAULT NULL,
  PRIMARY KEY (`isbn`,`language_id`),
  CONSTRAINT `book_properties_ibfk_1` FOREIGN KEY (`isbn`) REFERENCES `titles` (`isbn`)
) ;

#creating table country
CREATE TABLE `country` (
  `country_id` int(11) NOT NULL AUTO_INCREMENT,
  `country_name` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`country_id`)
) ;

#creating table state
CREATE TABLE `state` (
  `state_id` double NOT NULL,
  `state_name` varchar(255) DEFAULT NULL,
  `fk_country_id` int(5) NOT NULL,
  PRIMARY KEY (`state_id`,`fk_country_id`),
  KEY `fk_country_id_idx` (`fk_country_id`),
  CONSTRAINT `state_ibfk_1` FOREIGN KEY (`fk_country_id`) REFERENCES `country` (`country_id`)
) ;

#creating table city
CREATE TABLE `city` (
  `city_id` int(11) NOT NULL AUTO_INCREMENT,
  `city_name` varchar(255) DEFAULT NULL,
  `fk_state_id` double NOT NULL,
  PRIMARY KEY (`city_id`,`fk_state_id`),
  KEY `fk_state_id` (`fk_state_id`),
  CONSTRAINT `city_ibfk_1` FOREIGN KEY (`fk_state_id`) REFERENCES `state` (`state_id`)
) ;

#creating table user
CREATE TABLE `user` (
  `id` double NOT NULL,
  `first_name` varchar(255) DEFAULT NULL,
  `last_name` varchar(255) DEFAULT NULL,
  `fk_city_id` int(11) NOT NULL,
  `Dob` date DEFAULT NULL,
  `gender` varchar(6) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_city_id` (`fk_city_id`),
  CONSTRAINT `user_ibfk_1` FOREIGN KEY (`fk_city_id`) REFERENCES `city` (`city_id`)
) ;

#creating table purchase
CREATE TABLE `purchase` (
  `purchase_id` bigint(11) NOT NULL AUTO_INCREMENT,
  `fk_user_id` double DEFAULT NULL,
  `purchase_date` date DEFAULT NULL,
  PRIMARY KEY (`purchase_id`),
  KEY `fk_user_id` (`fk_user_id`),
  CONSTRAINT `purchase_ibfk_1` FOREIGN KEY (`fk_user_id`) REFERENCES `user` (`id`)
) ;

#creating table book_titles_has_purchase
CREATE TABLE `book_titles_has_purchase` (
  `book_titles_isbn` double NOT NULL,
  `purchase_purchase_id` bigint(11) NOT NULL,
  PRIMARY KEY (`book_titles_isbn`,`purchase_purchase_id`),
  KEY `fk_book_titles_has_purchase_purchase1_idx` (`purchase_purchase_id`),
  KEY `fk_book_titles_has_purchase_book_titles1_idx` (`book_titles_isbn`),
  CONSTRAINT `book_titles_has_purchase_ibfk_1` FOREIGN KEY (`book_titles_isbn`) REFERENCES `titles` (`isbn`),
  CONSTRAINT `book_titles_has_purchase_ibfk_2` FOREIGN KEY (`purchase_purchase_id`) REFERENCES `purchase` (`purchase_id`)
) ;

#creating table user_rating
CREATE TABLE `user_rating` (
  `fk_user_id` double NOT NULL,
  `isbn` double NOT NULL,
  `ratings` int(1) DEFAULT NULL,
  PRIMARY KEY (`fk_user_id`,`isbn`),
  KEY `isbn_idx` (`isbn`),
  CONSTRAINT `fk_user_id` FOREIGN KEY (`fk_user_id`) REFERENCES `user` (`id`),
  CONSTRAINT `isbn` FOREIGN KEY (`isbn`) REFERENCES `titles` (`isbn`)
) ;

