create view popular_themes as SELECT theme_name,
               Rank () 
                 OVER ( 
                   ORDER BY t3.popular_theme_count DESC) AS popular_theme_rank_in_trending_books 
        FROM  (SELECT theme_name,count(theme_name) as popular_theme_count from themes where isbn in (SELECT book_titles_isbn
FROM   (SELECT *, 
               Rank () 
                 OVER ( 
                   ORDER BY t1.copies_sold DESC) AS popular_isbns_rank 
        FROM   (SELECT book_titles_isbn, 
                       Count(book_titles_isbn) AS copies_sold 
                FROM   book_titles_has_purchase 
                GROUP  BY book_titles_isbn)t1) t2 where t2.popular_isbns_rank between 1 and 5) group by theme_name order by popular_theme_count desc ) t3 where t3.popular_theme_count between 1 and 5;  
				
